import React from "react";

class Pass extends React.Component{

    state = {
        name : "sohail",
        id : "1"
    }

    handleclick = (id) => {
        console.log(id)
    }

    // handlearg = () => {
    //     this.handleclick(this.state.id);
    // }

    render(){

        return(
            <React.Fragment>
                <h1>{this.state.name}</h1>
                <button onClick = {() => this.handleclick(this.state.id)} >Press</button>
            </React.Fragment>
        )

    }
}

export default Pass;
