import React, { useState, useEffect } from "react";

 function One(){

    const [count,countset] = useState(0);
    const [count1 , countset1] = useState(50);

    function O(){
        countset(count + 1);
    }

    function D(){
        countset1(count1 - 1);
    }


    useEffect(() => {
        console.log("useeffect Clicked")
    },[count])


    return(
        <React.Fragment>
            <h1>Count Up : {count}</h1>
            <button onClick = {O}>Increament</button>
            <h1>Count Down : {count1}</h1>
            <button onClick = {D}>Increament</button>
        </React.Fragment>
    )
 }

export default One;
