import React from "react";

class Child extends React.Component{
    delete = (event) => {
        let id = event.target.id;
        // console.log(id)

        var item = this.state.todo;
        item.splice(id,1);
        this.setState({
          todo: item,
          name : " "
        })
      }
    render(){

        return(
            <div>
                {
                    this.props.value.map((e,i) =>{
                        return(
                        <li className="color" key={i}>{e} <span onClick={this.delete}id={i}>X</span></li>
                        )
                      }
                    )
                }
            </div>
        )
    }
}


