import React from "react";

class StatePro extends React.Component{

    constructor(props){
        super(props)

        this.state = {
            name : props.name
        }
    }

        Click = (e) =>{
            e.preventDefault()
            this.setState({
                    name : "Sir Sohail Memon"
            })
        }

        changeHandler = (evnet) =>{
            console.log(evnet,"change")
        }

        render(){
            return(
                <React.Fragment>
                    <h1>{this.state.name}</h1>
                    <br />
                    <form onSubmit={this.Click}>
                    <input type = "text" value ={this.state.name} onChange={this.changeHandler} /> 
                    <button>Press</button>
                    </form>
                </React.Fragment>
            )
        }
}

export default StatePro;
