    state = {
        name : "sohail",
        id : "1"
    }

    handleclick = (name,id) => {
        console.log(name)
        console.log(id);
    }

    handlearg = () => {
        this.handleclick(this.state.name,id)
    }

    render(){

        return(
            <React.Fragment>
                {/* <h1>{this.state.name}</h1> */}
                <button onClick = {this.handlearg} >Press</button>
            </React.Fragment>
        )

    }
}

export default Pass;
