import React from "react";
import './App.css';

class StatePro extends React.Component{

    constructor(props){
        super(props)

        this.state = {
            name : props.name,
            todo : []
        };
    };

        Click = (e) =>{
            e.preventDefault();

            let todo = this.state.todo
            let input = this.state.name

            todo.push(input ,<br />);

            this.setState({
                    todo : todo,
                    name: ""
            });
        };

        changeHandler = (event) =>{
            console.log(event.target.value, "changehandlger")
            this.setState({
                    name: event.target.value
            });
        };

        render(){
            console.log(this.props, "props");
            console.log(this.state, "state");
            return(
                <React.Fragment>

                    <form onSubmit={this.Click}>
                    <input type = "text" value ={this.state.name} onChange={this.changeHandler} /> 
                    <button>Press</button>
                    </form>
                    <br />
            <li className="color">{this.state.todo}</li>
                    <h1>{this.state.name}</h1>
                    <br />
                </React.Fragment>
            )
        }
}

export default StatePro;
