import React from "react";

class StatePro extends React.Component{

    constructor(props){
        super(props)

        this.state = {
            name : props.name
        }
    }

        Click = () =>{
            this.setState({
                    name : "Sir Sohail Memon"
            })
        }

        render(){
            return(
                <React.Fragment>
                    <h1>{this.state.name}</h1>
                    <br />
                    <button onClick={this.Click}>Press</button>
                </React.Fragment>
            )
        }
}

export default StatePro;
